INSERT INTO power_up.games VALUES (12427, 'Fade to Black', null, 0, '1995-12-30');
INSERT INTO power_up.games VALUES (16778, 'Fading Hearts', 'An interactive story/game where you play the role of Ryou, a seemingly normal high school student in the land of Sorayama. Solve the mysteries that surround him while maintaining his friendships and saving Claire. Remain ignorant of the secrets and live a normal life or choose to learn the truth. Be careful where you put your priorities for it may prove fatal.', 0, '2014-01-02');
INSERT INTO power_up.games VALUES (12523, 'Faeria', 'Faeria is a strategic collectable card game that involves creating land on a board that is used to play cards and get to the opponent.
INSERT INTO power_up.games VALUES (9703, 'Faerie Solitaire', null, 0, '2010-09-16');
INSERT INTO power_up.games VALUES (10295, 'Faery: Legends of Avalon', 'Faery: Legends of Avalon is a big role playing game packed with a very distinctive ambience and graphic style. Create your character, embark in a great adventure and discover the fantastic world of Avalon!
INSERT INTO power_up.games VALUES (9624, 'Fags get high on weed (2001)', null, 0, null);
INSERT INTO power_up.games VALUES (1928, 'Fahrenheit', 'To find out why Lucas Kane killed a man in a diner in the interactive thriller Indigo Prophecy, the player takes the roles of Lucas Kane, Markus Kane, Lt. Carla Valenti and Sgt. Tyler Miles.', 0, '2005-09-15');
INSERT INTO power_up.games VALUES (6612, 'Fahrenheit', null, 0, '1995-08-31');
