INSERT INTO power_up.games VALUES (2668, 'FlatOut 2', 'DRIVING THIS RECKLESS IS NO ACCIDENT! But you might want to cause a few. In these high-speed races, the more damage you inflict, the better.', 0, '2006-06-29');
