INSERT INTO power_up.games VALUES (20330, 'Flinthook', 'Flinthook is a fast action-platformer with “roguelike” elements. Become space’s greatest pirate with your hookshot, pistol and slowmo powers!', 0, '2017-03-30');
INSERT INTO power_up.games VALUES (4857, 'Flip''s Twisted World', 'Flip’s Twisted World is a 3D puzzle-platformer that follows the adventures of Flip who is accidentally trapped inside a world where up and down are just a matter of perspective. By turning a magic cube, he is able to turn the entire world, opening up new paths and possibilities. Players must think “outside of the box” as they navigate the world that is literally in the palms of their hands.', 0, '2010-10-18');
