INSERT INTO power_up.games VALUES (4295, 'Foes of Ali', null, 0, '1995-12-30');
INSERT INTO power_up.games VALUES (15409, 'Folk Tale', 'Folk Tale is a single-player fantasy village builder RPG game in which you lead a ragtag band of peasants in growing a small settlement into a thriving market town, while dastardly minions plot your downfall. Sound the rallying call and head out into the wilds with parties of heroes and fight back the tide of evil in a game of endless possibilities.
