INSERT INTO power_up.games VALUES (17677, 'Foresight', 'Foresight is an epic space-based RTS game that throws you, the player, into the thick of the battle as a commander. Lead huge fleets, armed with fighters and bombers across wormholes to fight it out with your enemy. Launch bombing missions, fighter patrols, plant observation posts or fire support base with reconnaissance units. Intelligence is key.', 0, '2014-10-04');
INSERT INTO power_up.games VALUES (16314, 'Forestia : la grande aventure', 'An educational point-and-click adventure game about a forest where the player must solve problems the forest creatures have and learn about European flora and fauna within a number of minigames.', 0, '1997-12-31');
