INSERT INTO power_up.games VALUES (8558, 'Forza Motorsport 6', 'Experience unrelenting action in the most beautiful and comprehensive racing game of this generation. Collect, customize, and race over 450 Forzavista cars, all with working cockpits, opening doors and full damage. Compete in epic 24-player races across 26 world-famous locales. Master wet weather and night racing on your road to victory.', 0, '2015-09-14');
