INSERT INTO power_up.games VALUES (6996, 'Fracture', null, 0, '2008-10-06');
INSERT INTO power_up.games VALUES (8261, 'Fractured Soul', 'Fractured Soul takes the classic platformers of the 80s and doubles the action! Your unique ability to shift between two parallel dimensions allows you to weave through enemy fire, catch opponents by surprise, and leap over impossibly wide gaps by using platforms in both realities.', 0, '2014-02-24');
INSERT INTO power_up.games VALUES (9815, 'Fractured Space', 'Fractured Space is a 5-v-5 team-based space combat game. Control your gigantic capital ship to seize mines and bases for team resources and buffs. The intensity of a shooter combines with deep tactical decision making as you battle over five sectors to finally capture the enemy team''s home', 0, '2014-11-19');
