INSERT INTO power_up.games VALUES (17472, 'Full House Poker', 'A variation of the Texas Hold''em made for the Xbox 360.  Your "avatar" would play in Texas Hold''em tournaments against either AI players or other Xbox live players.  No real money was played for within the game.', 0, '2011-03-15');
