INSERT INTO power_up.games VALUES (11134, 'Gigantic', 'Gigantic is a visually-stunning evolution in team action gaming, where freedom of movement, play-style choice, and towering Guardians makes 5x5 competition more accessible and fun than ever.', 0, '2016-12-30');
