INSERT INTO power_up.games VALUES (16677, 'Gimbal', 'Gimbal is the multiplayer shooter with buildable vehicles. Craft the perfect ship, and then take to the skies with other pilots in real time combat. Cap flags, race ships, and blow each other up in a social multiplayer environment. Gimbal is the shooter you''ve been waiting for.', 0, '2013-10-01');
