INSERT INTO power_up.games VALUES (23282, 'Gloria Victis', 'Gloria Victis is a low fantasy Medieval MMORPG with an emphasis on realism. What does this mean? Open PvP with territory control, non-target combat, sandbox crafting, player-driven economy and no fireballs. All served up with a deep, mature story.', 0, null);
