INSERT INTO power_up.games VALUES (18192, 'Gradius Collection', 'Gradius Collection is a compilation of five beloved games in the Gradius series available for the first time on the PSP system. Spanning more than 20 years, the five games--Gradius, Gradius II, Gradius III, Gradius IV, and the long-awaited Gradius Gaiden--deliver hours of classic 2-D shooting action on the go.', 0, '2006-02-08');
INSERT INTO power_up.games VALUES (1484, 'Gradius Gaiden', null, 0, '1997-08-27');
INSERT INTO power_up.games VALUES (1478, 'Gradius II: Gofer no Yabō', null, 0, '1988-03-23');
