INSERT INTO power_up.games VALUES (21660, 'Grandia Xtreme', null, 0, null);
INSERT INTO power_up.games VALUES (21658, 'Grandia: Digital Museum', null, 0, null);
INSERT INTO power_up.games VALUES (21659, 'Grandia: Parallel Trippers', null, 0, null);
INSERT INTO power_up.games VALUES (14493, 'Grandmaster Chess', 'Grandmaster''s Chess game. This one with many options and more clever algorithm.', 0, null);
INSERT INTO power_up.games VALUES (11848, 'Granny''s Garden', null, 0, '1983-12-30');
INSERT INTO power_up.games VALUES (12878, 'Grapple', 'Grapple is a 3D puzzle platformer. You can fling, swing and climb across everything in Grapple. Different surfaces react uniquely to your gooball. Grapple is as fast paced as you want to play it. With multiple checkpoints and unlimited lives your skill is the only thing slowing you down.', 0, '2015-03-08');
INSERT INTO power_up.games VALUES (21106, 'Grappling Hook', null, 0, null);
INSERT INTO power_up.games VALUES (6256, 'Gratuitous Space Battles', null, 0, '2009-11-16');
INSERT INTO power_up.games VALUES (16334, 'Gratuitous Tank Battles', 'Gratuitous Tank Battles (GTB) is a unique hybrid of tower-defense, strategy, simulation and RTS. Set in an alternate history timeline where World War I never ends, you are the commander of allied forces fighting right up to the year 2114, where Mechs and lasers are used alongside tanks and rifles. The war continues across Europe, with the allied commanders still locked into the optimistic patriotism of the 1900s.', 0, '2012-04-03');
INSERT INTO power_up.games VALUES (18290, 'Grav', 'GRAV is a sand box experience where you explore a multitude of planets, hunt strange alien creatures, build and defend outposts, craft weapons and armor, and attempt to survive against other players in a hostile environment.', 0, '2015-01-07');
INSERT INTO power_up.games VALUES (7411, 'Grave', null, 0, null);
INSERT INTO power_up.games VALUES (10135, 'Gravi', 'Gravi is an intense puzzle-platformer that will test your tolerance for pain. Shoot and swing your way towards victory as Gravi, an adorable blue ball of energy. While you’re launching through terrorizing traps and bending gravity to your will, Anti-Gravi, an atomic ball of rage, is barreling down on your position.
INSERT INTO power_up.games VALUES (17246, 'Gravitar', 'Gravitar is a color vector graphics arcade game released by Atari, Inc. in 1982. The player controls a small blue spacecraft in a fictional solar system with several planets to explore. If the player moves his ship into a planet, he will be taken to a side-view landscape. Unlike many other shooting games, gravity plays a fair part in Gravitar: the ship will be pulled slowly to the deadly star in the overworld, and downward in the side-view levels. In the side-view levels, the player has to destroy red bunkers that shoot constantly, and can also use the tractor beam to pick up blue fuel tanks. Once all of the bunkers are destroyed, the planet will blow up, and the player will earn a bonus. Once all planets are destroyed, the player will move onto another solar system.', 0, '1982-12-30');
INSERT INTO power_up.games VALUES (18173, 'Graviteam Tactics: Mius Front', null, 0, '2016-03-03');
INSERT INTO power_up.games VALUES (17038, 'Graviteam Tactics: Operation Star', 'Graviteam Tactics: Operation Star is a sequel to Achtung Panzer: Kharkov 1943. It’s an Eastern Front war game with solid graphics and sprawling battlefields that really emphasizes the scope of WWII tank combat.
INSERT INTO power_up.games VALUES (15963, 'Gravitron 2', 'Gravitron 2 is a retro styled arcade gravity shooter in which you must pilot your way through some of the most devious terrains ever devised.', 0, '2008-08-01');
INSERT INTO power_up.games VALUES (21035, 'Gravitronix', null, 0, null);
