INSERT INTO power_up.games VALUES (18394, 'Gremlins, Inc.', 'Gremlins, Inc. is an intense strategy board game in a steampunk world of corrupt capitalist gremlins who compete for money, political power and prestige. Steal resources, extort bribes, manipulate elections, arrest other players and send them telegrams of misfortune… Unleash your inner gremlin!', 0, '2016-03-10');
