INSERT INTO power_up.games VALUES (16885, 'Gridiron Solitaire', 'Inspired by classic games like TV Sports Football, Front Page Sports Football, and Fairway Solitaire, Gridiron Solitaire combines football strategy with a solitaire-style card game for a new sports game experience. Lead your team to the Gridiron Bowl Championship.', 0, null);
