INSERT INTO power_up.games VALUES (19509, 'Halley Wars', null, 0, '1989-01-13');
INSERT INTO power_up.games VALUES (14461, 'Halloween Harry', 'Earth is under attack of alien spaceship. The aliens are changing all humans into mindless zombies. Harry is send to Earth to free all remaining hostages and find the source of the alien thread.', 0, null);
