INSERT INTO power_up.games VALUES (7891, 'Hammerwatch', 'Hammerwatch is an hack and slash action adventure, set in a fantasy pixel art environment! Play solo or co-op in this adventure from bottom to top of Castle Hammerwatch! Kill hordes of enemies with varied looks and features through four unique environments with traps, hidden secrets and puzzles!', 0, '2013-08-11');
