INSERT INTO power_up.games VALUES (7619, 'Hard Evidence: Marilyn Monroe Files', 'A documentary adventure game about the brightest pop in history, Marilyn Monroe.', 0, '1995-06-26');
INSERT INTO power_up.games VALUES (13871, 'Hard Hat Mack', 'Hard Hat Mack is a classic jumping arcade game. Your goal is to place all bricks to the right places without being kicked/killed or whatever by any other moving object or person. There are some special tools ready for you too.', 0, '1983-12-30');
INSERT INTO power_up.games VALUES (532, 'Hard Reset', 'In the only remaining human city of Bezoar, Major Fletcher, an Army Combat Veteran and soldier of the CLN, is dragged into a conflict between two of mankind''s greatest enemies. He is to discover that nothing is what it seems to be.
INSERT INTO power_up.games VALUES (17994, 'Hard Reset Redux', 'The world as we know it has ceased to exist. Surrounded by vast, hostile wastelands, the remnants of humankind''s dwindling population live within the closed city of Bezoar, but the walls have been breached. As a CLN soldier, you are all that stands between the hive mind machines of The Sanctuary, and total extinction.', 0, '2016-06-02');
