INSERT INTO power_up.games VALUES (3011, 'Harry Potter and the Order of the Phoenix', 'Harry Potter and the Order of the Phoenix is a video game that is based on the fifth installment of the popular Harry Potter series by J. K. Rowling and the film of the same name, for Microsoft Windows, PlayStation 2, PlayStation 3, Xbox 360, PSP, Nintendo DS, Wii, Game Boy Advance and Mac OS X. It was released in 2007 on 25 June in the U.S., 28 June in Australia and 29 June in the UK and Europe for PlayStation 3, PSP, PlayStation 2, Windows and the 3rd of July for most other platforms', 0, '2007-06-28');
