INSERT INTO power_up.games VALUES (22736, 'Hay Ewe', 'Hay Ewe is a ewe-nique puzzle adventure game centering on the adventures of Matilda the sheep! Navigate seriously challenging puzzles and avoid treacherous obstacles with your chain of lambs to deliver them to safety. Have fun using an array of boosts and cool items along the way. Get ready for a baaa-rilliant time and beware the farmageddon!', 0, null);
