INSERT INTO power_up.games VALUES (3686, 'Hello Kitty: Big City Dreams', null, 0, '2008-11-16');
INSERT INTO power_up.games VALUES (3689, 'Hello Kitty: Happy Party Pals', null, 0, '2005-10-10');
INSERT INTO power_up.games VALUES (7437, 'Hello Kitty: Picnic with Sanrio Friends', null, 0, '2012-11-01');
INSERT INTO power_up.games VALUES (3691, 'Hello Kitty: Roller Rescue', null, 0, '2004-03-30');
