INSERT INTO power_up.games VALUES (18201, 'Hi-way', 'HI-way is a single-player arcade game by Atari Inc., originally released in 1975. Marketed with the slogan “Hi Way — All It Needs Is Wheels”, it was Atari''s first game to use a cockpit cabinet. The aim is to dodge cars and negotiate turns down the road.', 0, '1975-12-30');
INSERT INTO power_up.games VALUES (294, 'Hidden & Dangerous', null, 0, '1999-07-28');
INSERT INTO power_up.games VALUES (107, 'Hidden & Dangerous 2', null, 0, '2003-10-20');
