INSERT INTO power_up.games VALUES (7607, 'Hollowpoint', 'Hollowpoint puts players in charge of secretive Organizations of corporate mercenaries, taking on missions for pay and prestige alongside three other players in online co-op. Developed by Ruffian Games, the team behind Crackdown 2, Hollowpoint brings classic 2D side-scrolling action into the next generation with seamless 3D targeting into stages’ backgrounds.', 0, null);
