INSERT INTO power_up.games VALUES (10415, 'Home Sheep Home 2', 'Join Shaun, Timmy and Shirley as they journey through over 60 levels of underground caverns, negotiate the busy streets of London and teleport through outer space all in search of the green, green grass of home. Use each sheep’s unique abilities to shove, leap, squeeze and swim your way through underground tunnels, abandoned mineshafts - and even evade the guards at Buckingham Palace!', 0, '2011-11-30');
INSERT INTO power_up.games VALUES (21793, 'Home Sweet Home', null, 0, null);
INSERT INTO power_up.games VALUES (1885, 'Homefront', 'Featuring a compelling single player story crafted by John Milius (Apocalypse Now, Red Dawn), Homefront immerses gamers in an interactive and cinematic FPS experience where they will assume an infantry role or take command of a wide variety of aerial and ground vehicles. In addition to the single-player experience, Homefront will deliver a robust multiplayer experience. In a land stripped of freedom, the brave will fight for their home.', 0, '2011-03-14');
INSERT INTO power_up.games VALUES (1886, 'Homefront: The Revolution', 'Homefront: The Revolution is an open-world first person shooter where you must lead the Resistance movement in guerrilla warfare against a superior military force.A living, breathing, open world responds to your actions - you and your Resistance Cell can inspire a rebellion on the streets and turn Occupation into Revolution, as oppressed civilians take up the fight.', 0, '2016-05-16');
