INSERT INTO power_up.games VALUES (17896, 'Horizon Shift', 'Horizon Shift is an explosive wave based, single screen shooter, with a ton of new ideas and platformer elements. Do what it takes to protect your horizon from the ever encroaching enemy attack! Use the unique risk reward multiplier system to maximise power-ups, build your bomb and destroy EVERYTHING!', 0, '2015-04-04');
INSERT INTO power_up.games VALUES (11156, 'Horizon: Zero Dawn', 'Horizon Zero Dawn, an exhilarating new action role playing game exclusively for the PlayStation 4 system, developed by the award winning Guerrilla Games, creatos of PlayStation''s venerated Killzone franchise. As Horizon Zero Dawn''s main protagonist Aloy, a skilled hunter, explore a vibrant and lush world inhabited by mysterious mechanized creatures.', 0, '2017-02-27');
