INSERT INTO power_up.games VALUES (15538, 'Hugo: The Forces of Nature', 'The media franchise Hugo includes numerous video games based on or inspired by an interactive television game show of the same title. The series of more than 30 games, including spin-offs, sold over 10 million copies for the Amiga, Commodore 64, PC, PlayStation, PlayStation 2, Game Boy, and mobile game systems. Most of the titles are platform games or minigame sets, but there are also some educational games.', 0, '2001-12-30');
