INSERT INTO games (id, name, summary, release) VALUES (18538, 'Zoo Tycoon', 'Zoo Tycoon is a business simulation video game developed by Frontier Developments and published by Microsoft Studios. The game was released on 22 November 2013 for Xbox One and Xbox 360.', '2013-11-21') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (334, 'Zoo Tycoon', 'Zoo Tycoon is a business simulation developed by Blue Fang Games and released by Microsoft Game Studios. It is a tycoon game in which the player must run a zoo and try to make a profit. Although first released for Microsoft Windows and Macintosh in 2001, it was ported to the Nintendo DS in 2005. It was followed by two expansion packs, Zoo Tycoon: Dinosaur Digs and Zoo Tycoon: Marine Mania, which were released in 2002, as well as a sequel, Zoo Tycoon 2, released in 2004.', '2001-10-16') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17622, 'Zoo Rampage', 'Did you ever wonder how it was like to be on the other side of the fence? In this zoo you can figure it out - take control over the whole area and destroy what kept you in prison for so long.', '2014-06-02') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (20277, 'Zoo Quest', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9293, 'Zoo Park', 'From the towering giraffe to lovable meerkats, in Zoo Park: Run Your Own Animal Sanctuary you''ll care for over 30 animals from all four corners of the Earth including lion, gorilla, coyote, puma, moose, raccoon, wolverine, snow leopard and the black bear.', '2014-06-12') ON CONFLICT DO NOTHING;
