INSERT INTO games (id, name, summary, release) VALUES (22806, 'Zombie City Defense 2', 'In a near future, the world has been devastated by a virus turning humanity into zombies. You are Iron Corps, the last defense of civilization traveling the world to find resources, help people and find answers.
