INSERT INTO games (id, name, summary, release) VALUES (22360, 'Ziba', 'This is a modern remake of the old calculator TI-84 game called BlockDude. The basic goal of the game is to move blocks to get over obstacles so you can reach your pink block. This will load the next puzzle.', '2017-03-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9168, 'Zhu Zhu Pets', null, '2010-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (23878, 'Zhombre, The Undead Hombre', 'Zhombre, The Undead Hombre is an Action Platformer set in a post apocalyptic world 100 years after the zombie invasion. Humans are the apex predator and the Undead have been forced to find refuge on the outskirts of human encampments. Zhombre wields his brothers severed leg in his fight against the human scourge. Follow him from The Barren Wastelands into the Neon city of the Living in his quest for vengeance as he uncovers the truth of the past.', null) ON CONFLICT DO NOTHING;
