INSERT INTO games (id, name, summary, release) VALUES (20164, 'Yoostar 2: In The Movies', 'Everyone now has the opportunity to become a global movie star with Yoostar 2. In an instant you can step into the shoes of famous characters from hundreds of movies, play out the part and then share your performance with the world.', '2011-03-07') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10031, 'Yooka-Laylee', 'Yooka-Laylee is an all-new 3D platformer from the creative talent behind the Banjo-Kazooie and Donkey Kong Country games. We’ve come together to form Playtonic Games and create a spiritual successor to our most cherished work from the past!INSERT INTO games (id, name, summary, release) VALUES (19020, 'Yon Paradox', 'Yon Paradox ​is a survival puzzle game where the hardest puzzle is yourself. Set in a cyber dimension, an antimatter-powered time machine broke, causing periodycal time rewinds. You will have to solve riddles to repair the time machine, while avoiding your past alter-egos to not create paradoxes.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25672, 'Yomi wo Saku Hana', 'New title from Experience Inc. set in the same world as Students of the Round and Demon Gaze. Set to be a Hack and Slash instead of a Dungeon Crawler. Exclusive to the Xbox One in Japan, might be released internationally.', '2017-06-29') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17189, 'Yomi', 'Yomi is a fighting game in card form. Learn your character, practice combos, and read the opponent''s tendencies. 10 characters to choose from, plus 10 more expansion characters available for purchase inside the app.', '2015-04-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11589, 'Yomawari: Night Alone', 'Yomawari is an upcoming Japanese horror action game by Nippon Ichi Software for the Playstation Vita. Yomawari features a female protagonist that is searching for her older sister and pet dog, while exploring a creepy town at night. She only has a flashlight at her disposal, and using said flash light will let her “see things that shouldn’t be seen.”', '2015-10-28') ON CONFLICT DO NOTHING;
