INSERT INTO games (id, name, summary, release) VALUES (8506, 'Xeodrifter', 'Xeodrifter is an action-packed exploration game with a retro graphical style. Blast your way through alien landscapes, seek out valuable secrets, and defeat ancient guardians to unlock powers from a legendary supreme culture in this exciting journey of mystery and adventure.', '2014-12-10') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26805, 'Xenosaga I & II', 'Xenosaga I &amp; II is a combined remake and retelling of Xenosaga Episode 1 and 2 redesigned for the Nintendo DS. The story changes were handled by the creator of the series.&#13;
INSERT INTO games (id, name, summary, release) VALUES (25021, 'Xenosaga Freaks', 'Xenosaga Freaks was only released in Japan and it contains 2 games, an Encyclopedia of terms used in Xenosaga Episode 1, and a demo for Xenosaga Episode 2.&#13;
