INSERT INTO games (id, name, summary, release) VALUES (573, 'X-Men Origins: Wolverine', 'X-Men Origins: Wolverine will give you new insight into how much damage indestructible, razor-sharp claws can do to a human body. This is a game that revels in gore, with decapitations, eviscerations, and mutilations drenching the screen in blood. It may be derivative, a cakewalk, and at times buggy, but the sheer visceral impact of the over-the-top violence in X-Men Origins is enough to make it a fun action game, as well as one of the better movie tie-ins released recently.', '2009-04-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4110, 'X-Men Mutant Academy 2', 'X-Men: Mutant Academy 2 is a PlayStation video game, the sequel to X-Men: Mutant Academy. Both feature the X-Men in a 3-D fighting arena similar to Mortal Kombat 4.
INSERT INTO games (id, name, summary, release) VALUES (4576, 'X-Men Legends II: Rise of Apocalypse', null, '2005-09-19') ON CONFLICT DO NOTHING;
