INSERT INTO games (id, name, summary, release) VALUES (4109, 'X-Men : Mutant Academy', 'X-Men: Mutant Academy is a 3-D/2D fighting game developed by Paradox, the company behind Activision''s Wu-Tang: Shaolin Style and the never-published Thrill Kill, and based on X-Men characters, from Marvel Comics. The game was released for the Game Boy Color and PlayStation as a tie-in to X-Men.
