INSERT INTO games (id, name, summary, release) VALUES (26175, 'Wuppo', 'Enter the wondrous world of Wuppo! Fight numerous enemies, collect countless items, and solve immersive puzzles, all while exploring the detailed history of the world in your quest to find a new home.', '2016-09-28') ON CONFLICT DO NOTHING;
