INSERT INTO games (id, name, summary, release) VALUES (18052, 'Wreckateer', 'Featuring Avatar FameStar! Wreckateer uses the Kinect to put you in control of a massive castle-wrecking Ballista. With the help of Wreck and Tinker, use 6 magic projectiles to travel the land and destroy 60 castles.', '2012-07-24') ON CONFLICT DO NOTHING;
