INSERT INTO games (id, name, summary, release) VALUES (8733, 'World of Speed', 'World of Speed is a new generation online racing game for true high-speed aficionados. Experience the thrill of street driving with amazing graphics and dynamic controls.', null) ON CONFLICT DO NOTHING;
