INSERT INTO games (id, name, summary, release) VALUES (7555, 'World of Guns: Gun Disassembly', 'World of Guns: Gun Disassembly lets you delve into the inner workings of guns from the largest gears to the smallest screws', '2014-05-20') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (942, 'World of Goo', 'World of Goo is a multiple award winning physics based puzzle / construction game made entirely by two guys. Drag and drop living, squirming, talking, globs of goo to build structures, bridges, cannonballs, zeppelins, and giant tongues. The millions of Goo Balls that live in the beautiful World of Goo are curious to explore - but they don''t know that they are in a game, or that they are extremely delicious. Congratulations, and good luck!', '2008-10-12') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11214, 'World of Final Fantasy', 'World of Final Fantasy combines classic RPG gameplay mechanics with fresh and imaginative toy-like visuals. As a brand-new title in the Final Fantasy franchise, this game can be enjoyed by both fans and newcomers to the series. Players will lead a pair of twins through varied Final Fantasy worlds and encounter the adorably familiar monsters therein. Capture and raise such familiar beasts as cactuar, chocobo, and behemoth to discover alternate forms and learn new abilities.', '2016-10-24') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9337, 'World of Diving', 'World of Diving is the Oculus Rift-enabled online diving game that brings you an exhilarating multiplayer diving adventure, all without getting wet. access the game via Early Access and start playing straight away!', '2014-08-25') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (27330, 'World of Darkness Preludes: Vampire and Mage', 'The classic roleplaying games Vampire: The Masquerade and Mage: The Ascension enter the digital present with a dual-pack of interactive fiction by a few of today’s most interesting fantasy writers.', '2017-02-15') ON CONFLICT DO NOTHING;
