INSERT INTO games (id, name, summary, release) VALUES (9540, 'Wonder Boy in Monster World', 'Monsters have taken over the land, and every day the citizens fear for their lives, hoping that a hero will step forth and bring peace to the embattled country.', '1991-10-24') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18277, 'Wonder Boy III: The Dragon''s Trap', 'A direct sequel to Wonder Boy in Monster Land, you wander through the lands using multiple forms that each have different abilities. Each defeated boss unlocks a new form that has to be used to advance to a new area, while shops give you access to new equipment for money.', '1989-12-30') ON CONFLICT DO NOTHING;
