INSERT INTO games (id, name, summary, release) VALUES (8686, 'William Shatner''s TekWar', 'William Shatner''s TekWar is a 1995 first-person shooter video game derived from the TekWar series of novels created by William Shatner and ghost-written by science-fiction author Ron Goulart.', '1995-09-29') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (23491, 'Will of the Gods', 'Will of the Gods is a strategical 1v1 arcade game where players play as Gods, each trying to guide as many followers as they can to their temple, while punishing those who choose to follow the other God. It''s a game about tactics, skill and proving who the better one is! Pick an arena, choose your game settings and activate gamemode mutators to create a chaotic duel between you and your rival. 
