INSERT INTO games (id, name, summary, release) VALUES (25875, 'Warsim', 'Warsim is a text-based kingdom management game with a heavy focus on control and random generation, each game will always be different, there are tens of thousands of randomly generated races, with quadrillions of randomly generated faces and flags and names and laws!
INSERT INTO games (id, name, summary, release) VALUES (17785, 'Warside', 'Warside is a visceral multiplayer Sci-Fi shooter featuring classic 2D gameplay combined with RPG elements. Play as part of a small squad of elite troops fighting for resources and power across the galaxy.', '2015-04-01') ON CONFLICT DO NOTHING;
