INSERT INTO games (id, name, summary, release) VALUES (16167, 'Voodoo Dice', 'Roll the dice in a mystical world filled with exploration, puzzle-solving, and adventure!  Mixing action based and puzzle mechanics in a 3D world, Voodoo Dice is an innovative, challenging puzzler that plays with your mind to lead you to solve puzzles in unique and interesting ways.', '2010-06-03') ON CONFLICT DO NOTHING;
