INSERT INTO games (id, name, summary, release) VALUES (1321, 'Turok: Dinosaur Hunter', 'A world where time has no meaning - and evil knows no bounds. Torn from a world long gone, the time traveling warrior Turok has found himself thrust into a savage land torn by conflict.', '1997-02-27') ON CONFLICT DO NOTHING;
