INSERT INTO games (id, name, summary, release) VALUES (16845, 'Turbo Dismount', 'Turbo Dismount is a kinetic tragedy about Mr. Dismount and the cars who love him. It is the official sequel to the wildly popular and immensely successful personal impact simulator - Stair Dismount.
INSERT INTO games (id, name, summary, release) VALUES (18510, 'Turbo', 'Racing game that leads you through several surroundings (e.g. cities, snowy landscapes, bridges, tunnels etc.).', '1981-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16191, 'Turba', 'Turba provides a gameplay experience unlike any you have encountered before. Load in music from your PC and combo blocks to your favorite tunes in this unique new twist on the “match-3” style game! The blocks on the game board generate and move to the beat of the song you choose. Clear blocks in time with the beat and make expert combos to maximize your score while you compete in online leaderboards for any and every song you play.', '2010-06-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (14439, 'Tunneler', 'Tunneler is the better known game by Geoffrey Silverton (the other one is Popgames). Two players using the same keyboard and the same screen each control an underground tank. Goal is to find and destroy the opponent''s tank. Since only small part of the map is displayed on the split screen, you might actually have some searching to do.', null) ON CONFLICT DO NOTHING;
