INSERT INTO games (id, name, summary, release) VALUES (18106, 'True Fantasy Live Online', 'True Fantasy Live Online is an MMORPG which was in development by Level-5 for the Microsoft Xbox. After a long and troubled development cycle that lasted almost two years, the game was cancelled on June 2, 2004. Available development and promotional information depict a massive fantasy setting in which up to 3,000 users - each with their own fully customizable character - can set out on adventures with one another, fighting monsters and collecting various items.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4216, 'True Crime: Streets of LA', null, '2003-11-02') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4215, 'True Crime: New York City', null, '2005-11-14') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10290, 'Trucks & Trailers', 'Trucks &amp; Trailers puts your driving skills to the ultimate test with 50 individual truck driving challenges. Compete against the clock to complete intricately designed courses and tasks that put you firmly into the driver''s seat. Can you avoid obstacles whilst reversing a trailer? Do you have the lateral thinking required to sort a series of trucks &amp; trailers in the yard into their correct positions? Can you park with precision?
INSERT INTO games (id, name, summary, release) VALUES (25506, 'Trucker 2', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25425, 'Trucker', null, null) ON CONFLICT DO NOTHING;
