INSERT INTO games (id, name, summary, release) VALUES (7218, 'Tomoyo After: It''s a Wonderful Life', 'Tomoyo After: It''s a Wonderful Life is a Japanese adult visual novel developed by Key. Key later released versions of Tomoyo After without the erotic content. The story follows the lives of Tomoya Okazaki, a young man who recently graduated from high school, and his close friend Tomoyo Sakagami as they start to see more of each other in a romantic relationship.', '2005-11-24') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (6403, 'Tomodachi Life', null, '2014-06-05') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15010, 'Tommy Tronic', 'Tommy Tronic - an old-skool platformer that isn''t trying to be anything else! Jumping, shooting, dodging and exploring is all that''s required to help get Tommy to his destination and free his tiny puppy from the clutches of an insane genius school-kid and his brutal sidekick ''Biffa''. It would be straight forward too, if it wasn''t for the fact that this journey will take Tommy through Gnarly wood; a twisted and eerie forest that adults avoid let alone tiny children! Imagination runs amok as Tommy confronts his childish fears head on, all in a bid to be reunited with his beloved pal ''Yapz''.', '2010-09-03') ON CONFLICT DO NOTHING;
