INSERT INTO games (id, name, summary, release) VALUES (12798, 'Tip Off', null, '1992-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10993, 'TinyKeep', 'In TinyKeep players will embrace the role of a hopeless prisoner held deep in a forgotten dungeon who one day wakes up to find themselves mysteriously released. Their savior is nowhere to be seen, but the cell door is broken and ajar, tempting them with the sweet scent of freedom. With nothing but a flickering lantern and a strange letter left behind by their in-mate, they must venture into the darkness and escape TinyKeep!
INSERT INTO games (id, name, summary, release) VALUES (3079, 'Tiny and Big: Grandpa''s Leftovers', 'Tiny &amp; Big, a comic styled jump and slice platformer, gives you the unique ability to shape a whole world at your will! You are Tiny, a nerdy inventor who tries to reclaim his most beloved possession: Grandpa''s white, fine rib underpants! On his journey through a forsaken desert he will meet mysterious creatures, no clowns, a taxi robot and his arch enemy: Big!', '2012-06-18') ON CONFLICT DO NOTHING;
