INSERT INTO games (id, name, summary, release) VALUES (11619, 'Tiny Troopers', 'Tiny Troopers puts you in command of a team of small soldiers. Guide them through 30 deadly missions with varied objectives: destroy enemy armoured formations, save hostages and eliminate enemy generals, to name but just a few.
