INSERT INTO games (id, name, summary, release) VALUES (18006, 'The Swindle', 'The Swindle is a steampunk cybercrime caper about breaking into buildings, hacking their systems, stealing all their cash, and quickly running away again before the police show up.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5892, 'The Swapper', 'The Swapper is a short puzzle platformer where you must complete every puzzle and collect 124 orbs, in groups of 3 and 9 later on, to complete the game.
INSERT INTO games (id, name, summary, release) VALUES (11590, 'The Surge', 'The Surge is a sci-fi spin on Dark Souls, from the Lords of the Fallen team. 
INSERT INTO games (id, name, summary, release) VALUES (9049, 'The Sun at Night', null, '2014-07-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17811, 'The Sun and Moon', 'The Sun and Moon, the winner of Ludum Dare 29, is a platformer with a unique mechanic: you can dive into the ground. Momentum is conserved but gravity is reversed, letting you fling yourself high into the air or deep beneath the surface. The goal is always straightforward - collect the three orbs in the level and jump into the wormhole - but spikes, endless drops and impossible heights make this more and more difficult.', '2014-10-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1863, 'The Sum of All Fears', null, '2002-05-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (6181, 'The Suffering: Ties That Bind', null, '2005-09-25') ON CONFLICT DO NOTHING;
