INSERT INTO games (id, name, summary, release) VALUES (27628, 'The Stuart Little 2', 'This game as counterpart of their movie with same title', '2002-07-08') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (23174, 'The Storm Guard: Darkness is Coming', 'The Storm Guard is a turn-based tactical game in a fantasy setting. Wildings, undead and even greater horrors from the north have crossed the borders and are now invading the realm of men causing death and destruction. You assume the role of the Lord Commander and have to manage this conflict.', '2016-08-24') ON CONFLICT DO NOTHING;
