INSERT INTO games (id, name, summary, release) VALUES (18453, 'The Stanley Parable Mod', 'The Stanley Parable is an experimental narrative-driven first person game. It is an exploration of choice, freedom, storytelling and reality, all examined through the lens of what it means to play a video game.', '2011-07-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (14544, 'The Stanley Parable Demonstration', 'The Stanley Parable Demonstration is supposedly the demo for The Stanley Parable, but has an original storyline and can be considered a short stand-alone game.', '2013-10-09') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3035, 'The Stanley Parable', 'The Stanley Parable is a first person exploration game. You will play as Stanley, and you will not play as Stanley. You will follow a story, you will not follow a story. You will have a choice, you will have no choice. The game will end, the game will never end. Contradiction follows contradiction, the rules of how games should work are broken, then broken again. This world was not made for you to understand.', '2013-10-16') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24786, 'The Standing Stones', null, '1983-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17579, 'The Stalin Subway: Red Veil', 'With the death of Stalin, all those who surrounded him, are now fighting for the supreme power and control over the nation. Lena, our heroine, has found that her husband has been kidnapped and possibly murdered. She has no clue about who did it, but she knows whom to ask.', '2014-09-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17580, 'The Stalin Subway', '"The Stalin Subway" is a First Person Shooter-style game where the events take place in post-war Moscow. The game allows to plunge into the depressing atmosphere of the Stalin''s period and to play as a Soviet officer.', '2014-08-04') ON CONFLICT DO NOTHING;
